mapper.insert:
insert into member values(
#{id},#{pw}, #{name},  #{email}, #{phone},
	#{post_code}, #{basic_addr},#{detail_addr}, sysdate, null, sysdate, #{birth} 
)

mapper.select:
select * from member
	where 
			id= #{id}
		and 
		email= #{email}
		and
		phone=#{phone}

mapper.update:
update member
	set
		pw = #{pw}
	where
		id = #{id}

mapper.select:
select * from member
	where 
			name= #{name}
		and 
		email= #{email}
		and
		phone=#{phone}


	select * from(
		select rownum rn, A.* from (
			select * from member
	
			
			where
				${type} like '%' || #{keyword} || '%'
			
	
	
			order  by id desc
		)A
	) where rn >= #{start} and rn <= #{end}
