mapper.select:
select * from (
select rownum rn, O.* from( select * from order_view
where 1=1
and
${item.type} like '%' || #{item.keyword} || '%'
and order_dt between to_date(#{dto.start_dt}, 'YYYY-MM-DD') and to_date(#{dto.end_dt}, 'YYYY-MM-DD') + 0.99999
and product_name like '%' || #{dto.product_name} || '%'
and pay_status = #{dto.pay_status}
and
t_status = #{tStat}
and
cs_status = #{csStat}
and member_id = #{dto.member_id}
and seller_id = #{dto.seller_id}
order by team desc)O
) where rn >= #{start} and rn <= #{end}

select count(*) from order_view where 1=1
and
${item.type} like '%' || #{item.keyword} || '%'
and pay_status = #{dto.pay_status}
and order_dt between to_date(#{dto.start_dt}, 'YYYY-MM-DD') and to_date(#{dto.end_dt}, 'YYYY-MM-DD') + 0.99999
and product_name like '%' || #{dto.product_name} || '%'
and
t_status = #{tStat}
and
cs_status = #{csStat}
and seller_id = #{dto.seller_id}

mapper.update:
update orders set

	t_status = #{t_status}

where 1=1

and
team=#{team}


and
order_id = #{order_id}

update orders set

	cs_status = #{cs_status}

where 1=1

and
team=#{team}


and
order_id = #{order_id}

update orders set

	pay_status = #{pay_status}

where 1=1

and
team=#{team}


and
order_id = #{order_id}

mapper.select:
select
order_id from orders where to_char(order_dt, 'YYYYMMDD') =
TO_CHAR(SYSDATE, 'YYYYMMDD') and id=#{id}

select * from (
select rownum rn, O.* from( select distinct team, member_name, pay_type, pay_status, re_name, re_phone, re_addr, t_invoice, t_id from order_view
where 1=1
and member_id=#{dto.member_id}
and seller_id=#{dto.seller_id}
and order_dt between to_date(#{dto.start_dt}, 'YYYY-MM-DD') and to_date(#{dto.end_dt}, 'YYYY-MM-DD') + 0.99999
and team = {team}
order by team desc)O)
where rn >= #{start} and rn <= #{end}

select * from (
select rownum rn, O.* from( select distinct team, member_name, pay_type, pay_status, re_name, re_phone, re_addr, t_invoice, t_id from order_view
where cs_status in('교환', '환불', '취소', '반품')
and member_id=#{dto.member_id}
and seller_id=#{dto.seller_id}
and order_dt between to_date(#{dto.start_dt}, 'YYYY-MM-DD') and to_date(#{dto.end_dt}, 'YYYY-MM-DD') + 0.99999
and team = {team}
order by team desc)O)
where rn >= #{start} and rn <= #{end}

select count(distinct team) from order_view where 1=1
and
${item.type} like '%' || #{item.keyword} || '%'
and order_dt between to_date(#{dto.start_dt}, 'YYYY-MM-DD') and to_date(#{dto.end_dt}, 'YYYY-MM-DD') + 0.99999
and product_name like '%' || #{dto.product_name} || '%'
and pay_status = #{dto.pay_status}
and t_status = #{dto.t_status}
and member_id = #{dto.member_id}
and seller_id = #{dto.seller_id}

select * from (
select rownum rn, O.* from( select distinct team, member_name, pay_type, pay_status, re_name, re_phone, re_addr, t_invoice, t_id from order_view
where 1=1
and
${item.type} like '%' || #{item.keyword} || '%'
and order_dt between to_date(#{dto.start_dt}, 'YYYY-MM-DD') and to_date(#{dto.end_dt}, 'YYYY-MM-DD') + 0.99999
and product_name like '%' || #{dto.product_name} || '%'
and pay_status = #{dto.pay_status}
and t_status = #{dto.t_status}
and member_id = #{dto.member_id}
and seller_id = #{dto.seller_id}
order by team desc)O
) where rn >= #{start} and rn <= #{end}

select count(distinct team) from order_view where 1=1

	and
	pay_status = #{pay_status}


	and
	t_status = #{t_status}


