mapper.select:
select * from (

	select rownum rn, O.* from(
		select * from order_view

where 1=1

	and
	
		${item.type} like '%' || #{item.keyword} || '%'
	


	and
	order_dt between to_date(#{dto.start_dt},
	'YYYY-MM-DD') and to_date(#{dto.end_dt}, 'YYYY-MM-DD') + 0.99999


	and
	product_name like '%' || #{dto.product_name} ||
	'%'


	and
	pay_status = #{dto.pay_status}


		and
	
		t_status = #{tStat}
	


		and
	
		cs_status = #{csStat}
	


	and
	member_id = #{dto.member_id}


	and
	seller_id = #{dto.seller_id}

)O

) where rn >= #{start} and rn <= #{end}

select count(*) from order_view where 1=1
and
${item.type} like '%' || #{item.keyword} || '%'
and pay_status = #{dto.pay_status}
and order_dt between to_date(#{dto.start_dt}, 'YYYY-MM-DD') and to_date(#{dto.end_dt}, 'YYYY-MM-DD') + 0.99999
and product_name like '%' || #{dto.product_name} || '%'
and
t_status = #{tStat}
and
cs_status = #{csStat}
and seller_id = #{dto.seller_id}

mapper.update:
update orders set
cs_status
= #{cs_status}
where team=#{team}

update orders set
status =
#{status}
where team=#{team}
