mapper.select:

select * from(
	select rownum rn, A.* from (
		select * from product_subcategory


		order by regist_dt desc
	)A
) where seller_id = #{seller_id} and rn >= #{start} and rn <= #{end}


select count(*) from product_subcategory where
	
	
	${type} like '%' || #{keyword} || '%' and
	
	
seller_id = #{seller_id}


	select * from(
		select rownum rn, A.* from (
			select * from product_subcategory
	
			
			where
				${type} like '%' || #{keyword} || '%'
			
	
			order by regist_dt desc
		)A
	) where seller_id = #{seller_id} and rn >= #{start} and rn <= #{end}
