mapper.select:

select * from(
	select rownum rn, A.* from (
		select * from product_subcategory

where 1=1

	and
	seller_id = #{seller_id}


		order by regist_dt desc
	)A
)



where rn >= #{start} and rn <= #{end}


select count(*) from product_subcategory where 1=1


	and ${type} like '%' || #{keyword} || '%'


	and seller_id = #{seller_id}


	select * from(
		select rownum rn, A.* from (
			select * from product_subcategory
	
	where 1=1
	
		and
		${type} like '%' || #{keyword} || '%'
	
	
		and
		seller_id = #{seller_id}
	
	
			order by regist_dt desc
		)A
	) 
	
	
	
	where rn >= #{start} and rn <= #{end}
