mapper.insert:
insert into seller values(
#{id}, #{pw}, #{name}, #{nickname}, #{email}, #{phone}, sysdate, null
)

mapper.select:
select * from seller
where
id= #{id}
and
email= #{email}

mapper.update:
update seller
set
pw = #{pw}
where
id = #{id}

mapper.select:
select * from seller
where
name= #{name}
and
email= #{email}
